//@title You are
//@by lusimeon

setcpm(60);

// DRUM
const drumKit = "RolandTR707";

const kick = sound("bd").bank(drumKit).struct("1 0 1 0").fast("1 0 1 0").velocity("0.8");
const snare = sound("sd").bank(drumKit).struct("0 1 0 0").fast("1").velocity("0.9").delay(0.1);
const hh = sound("ht").bank(drumKit).struct("<[0 0 0 1][0 1 0 1]>").velocity("0.6");
const oh = sound("mt").bank(drumKit).struct("<[1 0 0 1][1 0 1 0][1 1 0 1]>");
const drum_bass = stack(kick).color("red");
const drum_lead = stack(snare).color("blue");
const drum_hats = stack(hh, oh).color("green");
const drum = stack(
  drum_hats.gain(slider(1,0,1,1)),
  drum_lead.gain(slider(1,0,1,1)),
  drum_bass.gain(slider(0,0,1,1))
)
  .lpf(slider(8000,0,8000,200))
  .hpf(slider(0,0,6000,200));
// END DRUM

// NAPPE
const melody = n("<[0 2 4 [6 2]][6 3 2 [3 2]]>").scale("C:major").sound("gm_lead_3_calliope").color("cyan")
  .slow("2 3")
  .attack(0.05)
  .release(1.2)
  .lpf(800);
// END NAPPE

// PIANO
const piano = n("5 <3 2 1 6>").scale("C:major").sound("piano")
  .euclidLegato(5,8)
  .room(2)
  .delay("<0.4 1>")
  .slow("2")
  .hpf(0);
// END PIANO

// SPEAK
samples('shabda/speech:you')
samples('shabda/speech:are')
samples('shabda/speech:magnifique')
samples('shabda/speech:sexy')
samples('shabda/speech:tired')

samples('shabda/speech/fr-FR/f:je')
samples('shabda/speech/fr-FR/f:suis')
samples('shabda/speech/fr-FR/f:un')
samples('shabda/speech/fr-FR/f:nerd')
const speak = s("<[you ~][you are <magnifique tired sexy>]>")
  .slow("4")
  .late("0 0 0.15")
  .delay(0.1)
  .delayfeedback(3)
  .distort(1.2)
  .hpf(700);

// END SPEAK

speak: speak.postgain(slider(0.6,0,0.6,0.1));
drum: drum.postgain(slider(0.2,0,0.3,0.1));
piano: piano.postgain(slider(0.9,0,1,0.1));
